mapper.select:
	SELECT DEVELOP_SEQ
		  ,MEMBER_SEQ
		  ,TITLE
		  ,CONTENT
		  ,REG_DATE
		  ,UPDATE_DATE
		  ,DELETE_DATE
		  ,READ_COUNT
		  ,DEL_YN
	 FROM DEVELOP
    WHERE DEL_YN = 'N'
      AND MEMBER_SEQ = #{memberSeq}
      AND TITLE = #{title}
      AND CONTENT = #{content}
      AND REG_DATE = #{regDate}
    /* com.develop.dao.DevelopDao.selectDevelop */

mapper.insert:
INSERT 
INTO DEVELOP (
	 MEMBER_SEQ
	,TITLE
	,CONTENT
	,REG_DATE
	,UPDATE_DATE
	,READ_COUNT
	)
VALUES (
	 #{memberSeq}
	,#{title}
	,#{content}
	,NOW()
	,NOW()
	,0
)
/* com.develop.dao.DevelopDao.insertDevelop */

mapper.update:
UPDATE DEVELOP
   SET 
     TITLE = #{title}
	,CONTENT = #{content}
	,UPDATE_DATE = NOW()
WHERE DEVELOP_SEQ = #{developSeq}
